import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../utils';

const initialState = {
  user: {},
  media: {},
  loading: false,
};

const setIsLoading = () => {
  return updateObject(state, {loading: true})
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes:
      return;
    default:
      return state;
  }
};

export default reducer;
