import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ImageBackground,
  StatusBar,
} from 'react-native';
import AppButton from '../components/AppButton';
import Input from '../components/Input';
import { connect } from 'react-redux';
import * as actions from '../store/actions';

const image = {
  uri:
    'https://images.unsplash.com/photo-1559406041-c7d2b2e98690?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=668&q=80',
};
// not connected, mock up for login
const Login = (props) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const loginUser = async () => {
    const response = await props.onLogin({ email: email, password: password });
    console.log(props)
    if (response) props.navigation.navigate("Explore");
  };

  return (
    <View>
      <StatusBar hidden />
      <ImageBackground source={image} style={styles.backgroundImage}>
        <View style={styles.container}>
          <View style={styles.form}> 
            <Input
              label="Email"
              value={email}
              type="email"
              setAction={setEmail}
              placeholder="Enter email..."
            />
            <Input
              label="Password"
              value={password}
              type="password"
              setAction={setPassword}
              placeholder="Enter password..."
            />
          </View>
          <View style={styles.button}>
            <AppButton
              onPress={() => loginUser()}
              title="Sign In"
            />
          </View>
        </View>
        </ImageBackground>
    </View>
  );
};

const styles = StyleSheet.create({
  backgroundImage: {
    width: '100%',
    height: '100%',
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    height: 'calc(100%  - 20)',
    margin: 10,
    padding: 10,
  },
  form: {
    width: '100%',
    height: '80%',
    paddingTop: 300,
    display: 'flex',
    alignItems: 'center',
  },
  button: {
    width: '100%'
  }
});

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.general.user.token,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onLogin: (data) => dispatch(actions.loginUser(data)),
    onCreateUser: (data) => dispatch(actions.createUser(data)),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Login);
